import React, { useEffect, useState } from "react";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  Label,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col,
  Spinner
} from "reactstrap";
import Images from "utils/Images";



import { loginRequest, loginViaLinkedInRequest, loginViaGoogleRequest } from "./redux/actions";
import { connect } from "react-redux"
import { Link } from "react-router-dom"
import useForm from "../../utils/useForm"
import validator from "../../utils/validation"

import "./style.css"


import toast, { Toaster } from 'react-hot-toast';

import { OS, currentBrowser } from "utils/platform";

// import FacebookLogin from 'react-facebook-login';

import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'
import { LinkedIn } from 'react-linkedin-login-oauth2';



import { GoogleLogin } from 'react-google-login';
import { gapi } from "gapi-script";

const LoginScreen = (props) => {



  const clientId = "1048984404625-3kjheoht0ribe92j49a50kbnmettv0eu.apps.googleusercontent.com"
  useEffect(() => {
    function start() {
      gapi.client.init({
        clientId: clientId,
        scope: ""
      })
    };
    gapi.load('client:auth2', start);
  }, [])

  const { history, loginRequest, userData, loginViaLinkedInRequest, loginViaGoogleRequest, requesting } = props

  const [isAppleBtn, setAppleBtn] = useState(false)

  const [login, setLogin] = useState(false);
  const [FbData, setFbData] = useState(false);
  const [linkedInToken, setlinkedInToken] = useState(false);
  const [picture, setPicture] = useState('');

  const stateSchema = {
    email: {
      value: "",
      error: ""
    },
    password: {
      value: "",
      error: ""
    }
  }
  const validationStateSchema = {
    email: {
      required: true,
      validator: validator.email
    },
    password: {
      required: true
      //   validator: validator.password,
    }
  }

  const { state, handleOnChange, disable } = useForm(
    stateSchema,
    validationStateSchema
  )

  const handlelogin = () => {
    if (state.email.value && state.password.value) {
      const data = {
        username: state.email.value,
        password: state.password.value,
      }
      loginRequest(data)
    } else {
      toast.error('Both Fields are required');
    }
  }

  useEffect(() => {
    OS(window) == 'MacOS' && setAppleBtn(true)
  }, [])

  const responseFacebook = (response) => {
    setFbData(response);
